import React, { useEffect, useState } from 'react';
import './styles/App.css';

interface AuthResponse {
  success: boolean;
  message: string;
  error?: string;
}

function App() {
  const [status, setStatus] = useState<'initial' | 'success' | 'error'>('initial');
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Check if we have a code in the URL (OAuth callback)
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');

    if (code) {
      // We're in the callback route, show success
      setStatus('success');
      setMessage('Authorization successful! Pre-call preparation process has started. You will receive summaries via email shortly.');
      // Update the URL to remove the code parameter
      window.history.replaceState({}, '', window.location.pathname);
    }
  }, []);

  const handleSignIn = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/auth-url', {
        credentials: 'include',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (!data.url) {
        throw new Error('No authorization URL received');
      }

      window.location.href = data.url;
    } catch (error) {
      setStatus('error');
      setMessage('Failed to get authorization URL');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-8 space-y-4">
        <h1 className="text-3xl font-bold text-center text-gray-800">
          Pre-Call Preparation
        </h1>

        {status === 'initial' && (
          <div className="space-y-4">
            <p className="text-gray-600 text-center">
              Sign in with Google to enable automatic pre-call preparation summaries
            </p>
            <button
              onClick={handleSignIn}
              className="w-full flex items-center justify-center bg-blue-600 text-white rounded-lg px-4 py-2 hover:bg-blue-700 transition-colors"
            >
              Sign in with Google
            </button>
          </div>
        )}

        {status === 'success' && (
          <div className="text-center space-y-4">
            <div className="text-green-500 text-6xl">✓</div>
            <h2 className="text-xl font-semibold text-gray-800">Success!</h2>
            <p className="text-gray-600">{message}</p>
            <div className="mt-4 p-4 bg-blue-50 rounded-lg">
              <p className="text-sm text-blue-800">
                Your calendar is being analyzed for upcoming meetings. 
                Preparation summaries will be sent to your email address.
              </p>
              <p className="text-sm text-blue-600 mt-2">
                You can close this window now.
              </p>
            </div>
          </div>
        )}

        {status === 'error' && (
          <div className="text-center space-y-4">
            <div className="text-red-500 text-6xl">×</div>
            <h2 className="text-xl font-semibold text-gray-800">Error</h2>
            <p className="text-red-600">{message}</p>
            <button
              onClick={handleSignIn}
              className="text-blue-600 hover:text-blue-700"
            >
              Try Again
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
