import React, { useEffect, useState } from 'react';
import './styles/App.css';

interface AuthResponse {
  success: boolean;
  message: string;
  error?: string;
}

function App() {
  const [status, setStatus] = useState<'initial' | 'success' | 'error'>('initial');
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Check if we have a code in the URL (OAuth callback)
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');

    if (code) {
      // We're in the callback route, show success
      setStatus('success');
      setMessage('Authorization successful! Pre-call preparation process has started. You will receive summaries via email shortly.');
      // Update the URL to remove the code parameter
      window.history.replaceState({}, '', window.location.pathname);
    }
  }, []);

  const handleSignIn = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/auth-url', {
        credentials: 'include',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (!data.url) {
        throw new Error('No authorization URL received');
      }

      window.location.href = data.url;
    } catch (error) {
      setStatus('error');
      setMessage('Failed to get authorization URL');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex items-center justify-center p-4">
      <div className="max-w-md w-full bg-white rounded-xl shadow-xl p-8 space-y-6 border border-gray-100">
        <div className="flex flex-col items-center space-y-4">
          <img 
            src="https://awsmp-logos.s3.amazonaws.com/8dbda4db-e4f9-4e1d-b9d4-9f84dc105e1e/e64d79be1c781d5a94b07b772d0490f1.png" 
            alt="Cprime Logo" 
            className="h-12 object-contain mb-2"
          />
          <h1 className="text-3xl font-bold text-center text-gray-800">
            Pre-Call Preparation
          </h1>
          <div className="w-16 h-1 bg-blue-600 rounded-full"></div>
        </div>

        {status === 'initial' && (
          <div className="space-y-6">
            <p className="text-gray-600 text-center text-lg">
              Sign in with Google to enable automatic pre-call preparation summaries
            </p>
            <button
              onClick={handleSignIn}
              className="w-full flex items-center justify-center bg-blue-600 text-white rounded-lg px-6 py-3 hover:bg-blue-700 transition-all transform hover:scale-105 font-medium shadow-md"
            >
              <svg className="w-6 h-6 mr-2" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M12.545,10.239v3.821h5.445c-0.712,2.315-2.647,3.972-5.445,3.972c-3.332,0-6.033-2.701-6.033-6.032s2.701-6.032,6.033-6.032c1.498,0,2.866,0.549,3.921,1.453l2.814-2.814C17.503,2.988,15.139,2,12.545,2C7.021,2,2.543,6.477,2.543,12s4.478,10,10.002,10c8.396,0,10.249-7.85,9.426-11.748L12.545,10.239z"
                />
              </svg>
              Sign in with Google
            </button>
          </div>
        )}

        {status === 'success' && (
          <div className="text-center space-y-4">
            <div className="text-green-500 text-6xl">✓</div>
            <h2 className="text-xl font-semibold text-gray-800">Success!</h2>
            <p className="text-gray-600">{message}</p>
            <div className="mt-4 p-4 bg-blue-50 rounded-lg">
              <p className="text-sm text-blue-800">
                Your calendar is being analyzed for upcoming meetings. 
                Preparation summaries will be sent to your email address.
              </p>
              <p className="text-sm text-blue-600 mt-2">
                You can close this window now.
              </p>
            </div>
          </div>
        )}

        {status === 'error' && (
          <div className="text-center space-y-4">
            <div className="text-red-500 text-6xl">×</div>
            <h2 className="text-xl font-semibold text-gray-800">Error</h2>
            <p className="text-red-600">{message}</p>
            <button
              onClick={handleSignIn}
              className="text-blue-600 hover:text-blue-700"
            >
              Try Again
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
